SELECT self_service_request.reference_number, self_service_request.request_date,  CONCAT(usr.user_first_name, ' ', usr.user_last_name) AS requester_name,  pm.name as priority_name,   pc.problem_name,  self_service_request.subject,   self_service_request.created_date, CONCAT(u.user_first_name, ' ', u.user_last_name) AS assigned_to, CONCAT(uer.user_first_name, ' ', uer.user_last_name) AS created_by, am.asset_number, mainloc.title as locationname, reg.region_name, ss.status_name FROM self_service_request LEFT JOIN user as u ON u.uid = self_service_request.assigned_to LEFT JOIN user as uer ON uer.uid = self_service_request.created_by LEFT JOIN asset_main as am ON am.uid = self_service_request.asset_uid  LEFT JOIN priority_master as pm ON pm.uid = self_service_request.priority_uid  LEFT JOIN problem_category as pc ON pc.uid = self_service_request.problem_category_uid  LEFT JOIN user as usr ON usr.uid = self_service_request.requester_uid
LEFT JOIN link_user2address AS lua ON lua.useruid = self_service_request.requester_uid
LEFT JOIN location AS mainloc ON lua.addressuid = mainloc.uid
LEFT JOIN region AS reg ON mainloc.region_uid = reg.uid
LEFT JOIN service_status as ss ON ss.uid = self_service_request.status_uid